mapper.select:
select t.id as t_id,t.academyId as n_academyId,t.teacherName as t_teacherName,t.tel as t_tel,t.email as t_email,t.content as t_content,t.commentNumber as t_commentNumber,
c.id as c_id,c.teacherId as c_teacherId,c.nickname as c_nickname,c.identify as c_identify,c.ability as c_ability,c.funds as c_funds,c.relationship as c_relationship,c.future as c_future,c.thumbsUp as c_thumbsUp,c.thumbsDown as c_thumbsDown,c.createTime as c_createTime  
from teacher t,teacherComment c where t.id = c.teacherId and c.id = #{value}

select t.id as t_id,t.academyId as n_academyId,t.teacherName as t_teacherName,t.tel as t_tel,t.email as t_email,t.content as t_content,t.commentNumber as t_commentNumber, c.id as c_id,c.teacherId as c_teacherId,c.nickname as c_nickname,c.identify as c_identify,c.ability as c_ability,c.funds as c_funds,c.relationship as c_relationship,c.future as c_future,c.thumbsUp as c_thumbsUp,c.thumbsDown as c_thumbsDown,c.createTime as c_createTime from teacher t,teacherComment c where t.id = c.teacherId
and c.nickname like '%${nickname}%'
and c.teacherId = #{teacherId}
and c.content like '%${content}%'
order by c.createTime desc
limit #{offset},#{pageSize}
